var five = require("../lib/johnny-five.js"),
    compulsive = require("compulsive"),
    board, servos;

board = new five.Board();

board.on("ready", function() {

  function tap() {

    arm.move(0);

    compulsive.wait( 100, function() {
      arm.move(5);
    });
  }

  function dbl() {
    tap();

    compulsive.wait( 250, function() {
      tap();
    }.bind(this));
  }

  var action, claw, arm;

  action = new five.Servo(9);
  claw = new five.Servo({
    pin: 10,
    center: false
  });
  arm = new five.Servo({
    pin: 12,
    center: false
  });


  claw.move(120);
  arm.move(10);




  this.repl.inject({
    arm: arm,
    action: action,
    claw: claw,
    tap: tap,
    dbl: dbl
  });
});
