var five = require("../lib/johnny-five.js"),
    board, leds;

board = new five.Board();

board.on("ready", function() {

  leds = {
    a: new five.Led(2),
    b: new five.Led(3),
    c: new five.Led(4),
    d: new five.Led(5),
    e: new five.Led(6),
    f: new five.Led(7),
    g: new five.Led(8)
  };

  // "strobe" the led in 100ms on-off phases
  leds.a.strobe( 100 );
});
