function lapse( prev, next ) {
  return Math.round( Math.abs( next - prev ) / 60 * 160 );
}

// Tests
[
  { expect: 160, from: [ 0, 60 ] },
  { expect: 240, from: [ 0, 90 ] },
  { expect: 480, from: [ 0, 180 ] },
  { expect: 160, from: [ 60, 0 ] },
  { expect: 240, from: [ 90, 0 ] },
  { expect: 480, from: [ 180, 0 ] },

  { expect: 267, from: [ 40, 140 ] },
  { expect: 267, from: [ 140, 40 ] }

].forEach(function( test ) {
  var result = lapse.apply( null, test.from );

  console.log(
    result === test.expect ? "PASS" : "FAIL", [ result ]
  );
});
