var five = require("../lib/johnny-five.js");

(new five.Board()).on("ready", function() {
  // [
  //   { pin: 9, range: [ 90, 180 ] },
  //   { pin: 10, range: [ 90, 180 ] }
  // ].map( five.Servo );
  var servos, degree;

  // Initialize two servos on pins 9 and 10
  [ 9, 10 ].map( five.Servo );

  servos = new five.Servos();
  degree = 90;

  this.repl.inject({
    s: servos
  });

  five.Sequence([
    {
      loop: 500,
      task: function() {
        // Update the
        degree = degree === 90 ? 180 : 90;

        servos.move( degree );
      }
    }
  ]);
});
