var five = require("../lib/johnny-five"),
    board, lcd;

board = new five.Board();

board.on("ready", function() {

  lcd = new five.LCD({
    addr: 0x27
  });

  lcd.on("ready", function() {

    // Line 1: Hi rmurphey & hgstrp!
    lcd.clear().print("rmurphey, hgstrp");
  });

  this.repl.inject({
    lcd: lcd
  });

});
