Navigator.prototype.propose = function() {

  // Set speed to 2, Spin 5 seconds or until...
  this.fwd( 2 ).left( 5e3 );

  // the bot is facing north...
  this.compass.on("headingchange", function() {
    // Because thats where Rose will be standing
    if ( this.compass.bearing.abbr === "N" ) {
      // Stop the bot
      this.stop();

      // raise the grippers high
      this.gripper.max();

      // :D
    }
  }.bind(this));
};
